import React, { useState } from 'react';
import TextInput from '../../components/TextInput/TextInput';
import FileUpload from '../../components/FileUpload/FileUpload';
import AnalysisResults from '../../components/AnalysisResults/AnalysisResults';
import HumanApproval from '../../components/HumanApproval/HumanApproval';

interface AnalysisResult {
  taskDetected: boolean;
  taskDescription?: string;
  confidence?: number;
  specialist?: string;
  id?: string;
}

const Home: React.FC = () => {
  const [text, setText] = useState<string>('');
  const [files, setFiles] = useState<File[]>([]);
  const [isAnalyzing, setIsAnalyzing] = useState<boolean>(false);
  const [analysisResult, setAnalysisResult] = useState<AnalysisResult | null>(null);

  const handleTextChange = (newText: string) => {
    setText(newText);
  };

  const handleFileUpload = (uploadedFiles: File[]) => {
    setFiles([...files, ...uploadedFiles]);
  };

  const handleSubmit = async () => {
    if (!text && files.length === 0) {
      alert('Please enter text or upload files before submitting.');
      return;
    }

    setIsAnalyzing(true);

    // Simulate API call to backend
    setTimeout(() => {
      // Mock response
      const mockResult: AnalysisResult = {
        taskDetected: true,
        taskDescription: 'Review contract amendments for client Johnson & Co.',
        confidence: 0.89,
        specialist: 'Legal Researcher',
        id: `task-${Date.now()}`
      };

      setAnalysisResult(mockResult);
      setIsAnalyzing(false);
    }, 2000);
  };

  const handleApprove = () => {
    if (!analysisResult) return;

    // Simulate API call to store the approved task
    console.log('Task approved:', analysisResult);

    // Reset the form
    setText('');
    setFiles([]);
    setAnalysisResult(null);
  };

  const handleReject = () => {
    // Reset the analysis result
    setAnalysisResult(null);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Tender for Lawyers</h1>

      <div className="card mb-8">
        <h2 className="text-xl font-semibold mb-4">Input Content</h2>
        <TextInput 
          value={text} 
          onChange={handleTextChange} 
          placeholder="Paste your email, transcript, or message here..."
        />

        <div className="mt-6">
          <FileUpload onUpload={handleFileUpload} files={files} />
        </div>

        <div className="mt-6">
          <button 
            className="btn btn-primary"
            onClick={handleSubmit}
            disabled={isAnalyzing}
          >
            {isAnalyzing ? 'Analyzing...' : 'Submit for Analysis'}
          </button>
        </div>
      </div>

      {analysisResult && (
        <div className="mt-8">
          <AnalysisResults result={analysisResult} />
          <div className="mt-6">
            <HumanApproval 
              onApprove={handleApprove} 
              onReject={handleReject} 
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
