import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Home from './pages/Home/Home';
import TaskHistoryPage from './pages/TaskHistory/TaskHistory';
import Login from './pages/Login/Login';
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState<boolean>(false);

  // For demo purposes, we'll automatically authenticate the user
  // In a real app, this would be handled by a proper auth system
  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  return (
    <Routes>
      <Route
        path="/login"
        element={
          isAuthenticated ?
            <Navigate to="/" replace /> :
            <Login />
        }
      />

      <Route
        path="/*"
        element={
          <div className="min-h-screen flex flex-col bg-gradient-to-b from-white via-neutral-lightest to-white">
            <div className="absolute inset-0 bg-grid-pattern opacity-20 pointer-events-none"></div>
            <Header
              isAuthenticated={isAuthenticated}
              onLogout={() => setIsAuthenticated(false)}
            />
            <main className="flex-grow container mx-auto px-4 py-8 relative z-10">
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/history" element={<TaskHistoryPage />} />
              </Routes>
            </main>
            <Footer />
          </div>
        }
      />
    </Routes>
  );
}

export default App;
